import Foundationclass Car {var brand: Stringvar model: Stringvar year: Intvar hp: Int    init(brand:String, model:String, year:Int, hp:Int) {    self.brand = brand    self.model = model    self.year = year    self.hp =  hp    }    func printInfo() {        print(brand, model, year, hp)    }}class BMW: Car {    var acceleration: Double    init(brand:String, model:String, year:Int, hp:Int, acceleration:Double) {        self.acceleration = acceleration        super.init(brand:brand, model:model, year:year, hp:hp)    }     override func printInfo() {        print (brand, model, year, hp, acceleration)    }  }class Audi: Car {    var acceleration: Double        init(brand:String, model:String, year:Int, hp:Int,acceleration:Double) {        self.acceleration = acceleration        super.init(brand:brand, model:model, year:year, hp:hp)    }    override func printInfo() {        print (brand, model, year, hp, acceleration)    }}class Mercedes: Car {    var acceleration: Double    init(brand:String, model:String, year:Int, hp:Int, acceleration:Double) {        self.acceleration = acceleration        super.init(brand:brand, model:model, year:year, hp:hp)    }     override func printInfo() {        print (brand, model, year, hp, acceleration)    }}class Volkswagen: Car {    var acceleration : Double    init(brand:String, model:String, year:Int, hp:Int,acceleration:Double) {        self.acceleration = acceleration        super.init(brand:brand, model:model, year:year, hp:hp)    }     override func printInfo() {        print (brand, model, year, hp, acceleration)    }}    func createCar() -> [Car] {        let car1 = BMW(brand: "BMW", model: "X5", year: 2017, hp: 455, acceleration: 4.5)        let car2 = Audi(brand: "Audi", model: "A7", year: 2014, hp: 325, acceleration: 5.0)        let car3 = Mercedes(brand: "Mercedes", model: "E200", year: 2022, hp: 455, acceleration: 4.3)        let car4 = Volkswagen(brand: "Volkswagen", model: "Toureg", year: 2023, hp:305, acceleration: 9.2)        return [car1,car2,car3,car4]    }    func race() -> String {        let arrayCars: [Car] = createCar()        let opponent1 = Int.random(in: 1..<arrayCars.count)        let opponent2 = Int.random(in: 1..<arrayCars.count)        let winner = arrayCars[opponent1].hp > arrayCars[opponent2].hp ? opponent1 : opponent2        return "Между \(arrayCars[opponent1].brand) \(arrayCars[opponent1].model) и \(arrayCars[opponent2].brand) \(arrayCars[opponent2].model) Победитель : \(arrayCars[winner].brand)\(arrayCars[winner].model)"    }    print(race())